import React, { useEffect, useState } from 'react'
import PostDetail from './PostDetail'
import './Profile.css'
import { useNavigate } from 'react-router-dom'
import ProfilePic from './ProfilePic'

const Profile = () => {
  const [pics, setPics] = useState([])
  const [show, setShow] = useState(false)
  const [posts, setPosts] = useState([])
  const [changePic, setChangePic] = useState(false)
  const navigate = useNavigate()
  const[user,setUser]=useState('')
  const toggleDetails = (posts) => {
    if (show) {
      setShow(false);
    } else {
      setShow(true);
      setPosts(posts);
    }
  };

  const changeProfile = () => {
    if (changePic) {
      setChangePic(false)
    }
    else {
      setChangePic(true)
    }
  }

  useEffect(() => {

    const token = localStorage.getItem("jwt");
    if (!token) {
      navigate("/signin");
    }
    fetch(`http://localhost:5000/user/user/${JSON.parse(localStorage.getItem('user')).name }`, {
      headers:
      {
        "Authorization": "Bearer " + localStorage.getItem('jwt')
      }

    })
      .then(res => res.json())
      .then(result => {
        setPics(result.post)
        setUser(result.user)
      })

  }, [])
  return (
    <div className='profile'>
      <div className="profile-frame">
        <div className="profile-pic">
          <img style={{ cursor: "pointer" }} onClick={changeProfile} src="https://images.unsplash.com/photo-1698956197153-f14f37bd7dd4?auto=format&fit=crop&q=80&w=1887&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="" />
        </div>
        <div className="profile-data">
          <h1>{JSON.parse(localStorage.getItem('user')).name} </h1>
          <div className="profile-info">
            <p>40 Posts</p>
            <p>40 followers</p>
            <p>40 following </p>
          </div>
        </div>
      </div>
      <hr style={{ width: "90%", opacity: "0.8", margin: "25px auto " }} />
      <div className="gallery">
        {
          pics.map((pic) => {
            return <img key={pic._id} src={pic.photo} className='item' onClick={() => {
              toggleDetails(pic)
            }}></img>

          })
        }


      </div>

      {show && <PostDetail item={posts} toggleDetails={toggleDetails} />}
      {changePic && <ProfilePic changeprofile={changeProfile} />}


    </div>
  )
}

export default Profile
