import React, { useEffect, useState } from 'react'
import PostDetail from './PostDetail'
import './Profile.css'
import { useParams } from 'react-router-dom'

const UserProfile = () => {
    const { userid } = useParams()
    const [user, setUser] = useState("")
    const [pics, setPics] = useState([])
    const [show, setShow] = useState(false)
    const [posts, setPosts] = useState([])
    const [isFollow, setIsFollow] = useState(false)

    const followUser = (userId) => {
        fetch('http://localhost:5000/follow', {
            method: "put",

            headers: {
                "Content-Type": "application/json",
                Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
            body: JSON.stringify({
                followId: userId
            })


        })
            .then((res) => res.json())
            .then((data) => {
                setIsFollow(true)
            })

    }
    const unfollowUser = (userId) => {
        fetch('http://localhost:5000/unfollow', {
            method: "put",

            headers: {
                "Content-Type": "application/json",
                Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
            body: JSON.stringify({
                followId: userId
            })


        })
            .then((res) => res.json())
            .then((data) => {
                setIsFollow(false)
            })

    }




    useEffect(() => {

        fetch(`http://localhost:5000/user/${userid}`, {
            headers:
            {
                "Authorization": "Bearer " + localStorage.getItem('jwt')
            }

        })
            .then(res => res.json())
            .then(result => {
                setUser(result.user)
                setPosts(result.post)
                if (result.user.followers.includes(
                    JSON.parse(localStorage.getItem("user"))._id
                    ))
                    {
                    setIsFollow(true)
                }

            })

    }, [])
    return (
        <div className='profile'>
            <div className="profile-frame">
                <div className="profile-pic">
                    <img src="https://images.unsplash.com/photo-1698956197153-f14f37bd7dd4?auto=format&fit=crop&q=80&w=1887&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="" />
                </div>
                <div className="profile-data">
                    <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}>
                        <h1>{user.name}</h1>
                        <button className='followBtn' onClick={() => {

                            if (isFollow) {
                                unfollowUser(user._id)
                            }
                            else {
                                followUser(user._id)
                            }


                        }}>
                            {isFollow ? "Unfollow" : "Follow"}

                        </button>
                    </div>

                    <div className="profile-info">
                        <p>{posts.length} Posts</p>
                        <p>{user.followers?posts.user.length:"0"} Followers</p>
                        <p>{user.following?user.following.length:"0"} following </p>
                    </div>
                </div>
            </div>
            <hr style={{ width: "90%", opacity: "0.8", margin: "25px auto " }} />
            <div className="gallery">
                {
                    posts.map((pic) => {
                        return (
                            <img src={pic.photo} key={pic._id} className='item'></img>
                        )
                    })

                }


            </div>

            {/* {show && <PostDetail item={posts} toggleDetails={toggleDetails}/>} */}


        </div>
    )
}

export default UserProfile
