    const loginStatus = () => {
        const token = localStorage.getItem('jwt')

        if (token) {
            return [
                <>
                    <Link to="/profile">
                        <li>Profile</li>
                    </Link>
                    <Link to="/createPost">
                        <li>Create Post</li>
                    </Link>
                    <Link to="/logout">
                        <li ><button>Logout</button></li>
                    </Link>

                </>
            ]

        }
        else {
            return [
                <>
                    <Link to="/signup">
                        <li>Signup</li>
                    </Link>
                    <Link to="/signin">
                        <li>Signin</li>
                    </Link>
                </>
            ]

        }
    }
    return (
        <div className='navbar'>
            <img src={logo} alt="" />
            <ul className="nav-menu">
                {loginStatus()}

            </ul>
        </div>
    )
}

export default Navbar
