import React ,{useEffect,useState} from 'react'
import PostDetail from './PostDetail'
import './Profile.css'

const Profile = () => {
  const[pics,setPics]=useState([])
  const [show,setShow]=useState(false)
  const [posts,setPosts]=useState([])
  const toggleDetails= (posts) => {
    if (show) {
      setShow(false);
    } else {
      setShow(true);
      setPosts(posts);
    }
  };

  useEffect(()=>{

    const token = localStorage.getItem("jwt");
    if (!token) {
      navigate("/signin");
    }
    fetch('http://localhost:5000/myposts',{
      headers:
      {
        "Authorization":"Bearer "+localStorage.getItem('jwt')
      }

    })
    .then(res=>res.json())
    .then(result=>setPics(result))

  },[])
  return (
    <div className='profile'>
      <div className="profile-frame">
        <div className="profile-pic">
        <img src="https://images.unsplash.com/photo-1698956197153-f14f37bd7dd4?auto=format&fit=crop&q=80&w=1887&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="" />
        </div>
        <div className="profile-data">
          <h1>Abhishek </h1>
          <div className="profile-info">
            <p>40 Posts</p>
            <p>40 followers</p>
            <p>40 following </p>
          </div>
        </div>
      </div>
      <hr style={{width:"90%",opacity:"0.8",margin:"25px auto "}} />
      <div className="gallery">
        {
          pics.map((pic)=>{
           return <img key={pic._id} src={pic.photo} className='item' onClick={()=>{
            toggleDetails(pic)
           }}></img>

          })
        }


      </div>

      {show && <PostDetail item={posts} toggleDetails={toggleDetails}/>}


    </div>
  )
}

export default Profile
