import React, { useState, useEffect } from 'react'
import './Home.css'
import { useNavigate } from 'react-router-dom'
// import { post } from '../../../backend/routes/auth'

const Home = () => {

  const navigate = useNavigate()
  const [data, setData] = useState([])
  const [comment, setComment] = useState("")
  const [show, setShow] = useState(false)
  const [item,setItem]=useState([])
  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      navigate("/signin");
    }

    fetch("http://localhost:5000/allposts", {
      headers:
      {
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      }
    })
      .then(res => res.json())
      .then(result => setData(result))
      .catch(err => console.log(err))
  }, [])


  const toggleComment = (posts) => {
    if (show) {
      setShow(false)
    }
    else {
      setItem(posts)
      console.log(item);
      setShow(true)
    }
  }


  const likePost = (id) => {
    fetch('http://localhost:5000/like', {
      method: "put",
      headers:
      {
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem('jwt')
      },
      body: JSON.stringify({
        postId: id
      })
    })
      .then(res => res.json())
      .then((result) => {
        // console.log(result);
        const newData = data.map((posts) => {
          if (posts._id === result._id) {
            return result
          }
          else {
            return posts
          }
        })
        setData(newData)

      })

  }
  const unlikePost = (id) => {
    fetch('http://localhost:5000/unlike', {
      method: "put",
      headers:
      {
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem('jwt')
      },
      body: JSON.stringify({
        postId: id
      })
    })
      .then(res => res.json())
      .then((result) => {
        const newData = data.map((posts) => {
          if (posts._id === result._id) {
            return result
          }
          else {
            return posts
          }
        })
        setData(newData)
        // console.log(result);

      })

  }

  const makeComment = (text, id) => {
    fetch('http://localhost:5000/comment', {
      method: "put",
      headers:
      {
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem('jwt')
      },
      body: JSON.stringify({
        text: text,
        postId: id
      })
    })
      .then(res => res.json())
      .then((result) => {
        // console.log(result);
      })


  }


  return (
    <div className='home'>
      {data.map((posts) => {
        return (
          <div className="card">
            <div className="card-header">
              <div className="card-pic">
                <img src="https://images.unsplash.com/photo-1698956197153-f14f37bd7dd4?auto=format&fit=crop&q=80&w=1887&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="" />
              </div>
              <h5>{posts.postedBy.name}</h5>

            </div>
            <div className="card-image">
              <img src={posts.photo} alt="" />
            </div>
            <div className="card-content">
              {

                posts.likes.includes(JSON.parse(localStorage.getItem("user"))._id) ?
                  (<span className="material-symbols-outlined material-symbols-outlined-red" onClick={() => { unlikePost(posts._id) }}>favorite</span>)
                  : (<span className="material-symbols-outlined" onClick={() => { likePost(posts._id) }}>favorite</span>)
              }

              <p>{posts.likes.length} Likes</p>
              <p>{posts.body}</p>
              <p style={{ fontWeight: "bold", cursor: "pointer" }} onClick={
                () => toggleComment(posts)
              }>View all comments</p>
            </div>
            <div className="add-comment">
              <span className="material-symbols-outlined">
                mood
              </span>
              <input type="text" name="" id="" placeholder='add a comment' value={comment}
                onChange={(e) => { setComment(e.target.value) }}
              />
              <button className="comment" onClick={() => { makeComment(comment, posts._id) }}>Post</button>
            </div>

          </div>

        )

      })}


      {/* show all comments */}
      {
        show && (


          <div className="showComment">
            <div className="container">
              <div className="postPic">
                <img src={item.photo} alt="" />

              </div>
              <div className="details">

                <div className="card-header">
                  <div className="card-pic">
                    <img src="https://images.unsplash.com/photo-1698956197153-f14f37bd7dd4?auto=format&fit=crop&q=80&w=1887&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="" />
                  </div>
                  <h5>{item.postedBy.name}</h5>

                </div>

                <div className="comment-section">
                  <p className='comm'>
                    <span className='commenter' style={{ fontWeight: "bolder" }}>Abhishek </span>
                    <span className='commentText'>Abhishek pic </span>
                  </p>
                  <p className='comm'>
                    <span className='commenter' style={{ fontWeight: "bolder" }}>Abhishek </span>
                    <span className='commentText'>Abhishek pic </span>
                  </p>
                  <p className='comm'>
                    <span className='commenter' style={{ fontWeight: "bolder" }}>Abhishek </span>
                    <span className='commentText'>Abhishek pic </span>
                  </p>
                  <p className='comm'>
                    <span className='commenter' style={{ fontWeight: "bolder" }}>Abhishek </span>
                    <span className='commentText'>Abhishek pic </span>
                  </p>
                  <div className="card-content">
                    <p>{item.likes.length} Likes</p>
                    <p>{item.body}</p>
                  </div>
                  <div className="add-comment">
                    <span className="material-symbols-outlined">
                      mood
                    </span>
                    <input type="text" name="" id="" placeholder='add a comment' value={comment}
                      onChange={(e) => { setComment(e.target.value) }}
                    />
                    <button className="comment"
                    // onClick={() => { makeComment(comment, posts._id) }}
                    >Post</button>
                  </div>

                </div>


              </div>
            </div>
            <div className="close-comment" onClick={()=>{toggleComment()}}>
              <span className="material-symbols-outlined material-symbols-outlined-comment">
                close
              </span>

            </div>

          </div>
        )
      }

    </div>
  )
}

export default Home
