import React, { useContext } from 'react'
import logo from '../img/logo.png'
import './NavBar.css'
import { Link ,useNavigate} from 'react-router-dom'
import { LoginContext } from '../context/LoginContext'



const Navbar = () => {
    const {setModalOpen}=useContext(LoginContext)



    const loginStatus = () => {
        const token = localStorage.getItem('jwt')

        if (token) {
            return [
                <>
                    <Link to="/profile">
                        <li>Profile</li>
                    </Link>
                    <Link to="/createPost">
                        <li>Create Post</li>
                    </Link>
                    <Link to={""}>
                        <button className='primaryBtn' onClick={()=>{localStorage.clear()}}>Logout</button>
                    </Link>

                </>
            ]

        }
        else {
            return [
                <>
                    <Link to="/signup">
                        <li>Signup</li>
                    </Link>
                    <Link to="/signin">
                        <li>Signin</li>
                    </Link>
                </>
            ]

        }
    }
    return (
        <div className='navbar'>
            <img src={logo} alt="" />
            <ul className="nav-menu">
                {loginStatus()}

            </ul>
        </div>
    )
}

export default Navbar
