import React, { useState } from 'react'
import './CreatePost.css'

function CreatePost() {

  const [body, setBody] = useState('')
  const [image, setImage] = useState('')
  const loadfile = (event) => {
    var output = document.getElementById('output');
    output.src = URL.createObjectURL(event.target.files[0]);
    output.onload = function () {
      URL.revokeObjectURL(output.src)
    }
  }
  return (
    <div className='createPost'>
      <div className="post-header">
        <h4>Create new Post</h4>

        <button id="post-btn">Share</button>
      </div>
      <div className="main-div">
        <img src="data:image/png;base64," alt="" id='output' />
        <input type="file" accept='image/*' onChange={(event) => { 
          loadfile(event)
          setImage(e.target.files)

          }} />

      </div>
      <div className="details">
        <div className="card-header">
          <div className="card-pic">
            <img src="https://images.unsplash.com/photo-1698956197153-f14f37bd7dd4?auto=format&fit=crop&q=80&w=1887&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="" />
          </div>
          <h5>name of user</h5>
        </div>
        <textarea value={body}
          onchange={(e) => { setBody(e.target.value) }}
          type='text' placeholder='write a caption .....'></textarea>

      </div>
    </div>
  )
}

export default CreatePost
