import React, { useState, useEffect } from 'react'
import './Home.css'
import { useNavigate } from 'react-router-dom'

const Home = () => {
  const navigate = useNavigate()
  const[data,setData]=useState([])
  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      navigate("./signup");
    }

    fetch("http://localhost:5000/allposts", {
      headers:
      {
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      }
    })
    .then(res=>res.json())
    .then(result=>setData(result))
    .catch(err=>console.log(err))
  }, [])


  return (
    <div className='home'>
      {data.map((posts)=>{
        console.log(posts);

      })}
      <div className="card">
        <div className="card-header">
          <div className="card-pic">
            <img src="https://images.unsplash.com/photo-1698956197153-f14f37bd7dd4?auto=format&fit=crop&q=80&w=1887&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="" />
          </div>
          <h5>hello</h5>

        </div>
        <div className="card-image">
          <img src="https://images.unsplash.com/photo-1698949767382-a275233b54b4?auto=format&fit=crop&q=80&w=1911&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="" />
        </div>
        <div className="card-content">
          <span className="material-symbols-outlined">
            favorite
          </span>
          <p>1 Like </p>
          <p>This is amazing </p>
        </div>
        <div className="add-comment">
          <span className="material-symbols-outlined">
            mood
          </span>
          <input type="text" name="" id="" placeholder='add a comment' />
          <button className="comment">Post</button>
        </div>

      </div>
    </div>
  )
}

export default Home
