                    <div className="add-comment">
                        <span className="material-symbols-outlined">mood</span>
                        <input
                            type="text"
                            placeholder="Add a comment"
                            // value={comment[`${item._id}`] ? comment[`${item._id}`] : ""}
                            name={item._id}
                            // onChange={(e) => {
                            //     setComment({
                            //         [e.target.name]: e.target.value
                            //     });
                            // }}
                        />
                        <button
                            className="comment"
                            // onClick={() => {
